package cl.ubb.agil;

public class Calculadora {

	public Calculadora(){
		
	}

	public int suma(int num1, int num2) {
		// TODO Auto-generated method stub
		return (num1+num2);
	}

	public int resta(int num1, int num2) {
		// TODO Auto-generated method stub
		return (num1-num2);
	}

	public int multiplica(int i, int j) {
		// TODO Auto-generated method stub
		return i*j;
	}

	public int divide(int i, int j) {
		// TODO Auto-generated method stub
		return i/j;
	}
	
	
	
	
}
